sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

   //Type appropriate comment here, and begin script below
   var oldVal = "";
   var newVal = "";

	if (oldValue === "hr") {
		oldVal = "Human Resources";
	} else if (oldValue === "facilities") {
		oldVal = "Facilities";
	} else if (oldValue === "legal") {
		oldVal = "Legal";
	} else {
		oldVal = oldValue;
	}

	if (newValue === "hr") {
		newVal = "Human Resources";
	} else if (newValue === "facilities") {
		newVal = "Facilities";
	} else if (newValue === "legal") {
		newVal = "Legal";
	} else {
		newVal = newValue;
	}

		g_form.addInfoMessage("Request Type changed from " +oldVal+" to "+newVal+ " .");
	}
